    public function getSourceContext()
    {
        return new Source("<!DOCTYPE html>
<html>
<head>
    <meta charset=\"UTF-8\">
    <title>{% block title %}Se connecter {% endblock %}</title>
    <link rel=\"icon\"
          href=\"data:image/svg+xml,<svg xmlns=%22http://www.w3.org/2000/svg%22 viewBox=%220 0 128 128%22><text y=%221.2em%22 font-size=%2296%22>⚫️</text></svg>\">
    {# Run `composer require symfony/webpack-encore-bundle` to start using Symfony UX #}
    {% block stylesheets %}
        {{ encore_entry_link_tags('app') }}
    {% endblock %}

    {% block javascripts %}
        {{ encore_entry_script_tags('app') }}
    {% endblock %}
</head>
<body style=\"overflow:hidden;\">
<div id=\"auth\">

    <div class=\"row h-100\">
        <div class=\"col-lg-5 col-12\">
            <div id=\"auth-left\" style=\"text-align: center;\">
                <div class=\"auth-logo\" >
                    <a href=\"{{path('app_home')}}\"><img src=\"{{ asset('uploads/logo/j4r_logo.png')}}\" alt=\"Logo\" style=\"height: 100%;\"></a>
                </div>
                <h1 class=\"auth-title\">Se connecter.</h1>
                <p class=\"auth-subtitle mb-5\">Connectez-vous avec votre adresse email J4R.</p>

                <form method=\"post\">
                    {% if error %}
                        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
                    {% endif %}

                    {% if app.user %}
                        <div class=\"mb-3\">
                            You are logged in as {{ app.user.username }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
                        </div>
                    {% endif %}
                    <div class=\"form-group position-relative has-icon-left mb-4\">
                        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\"  autocomplete=\"email\" required autofocus class=\"form-control form-control-xl\" placeholder=\"Email\"  style=\"background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;\">
                        <div class=\"form-control-icon\">
                            <i class=\"bi bi-person\"></i>
                        </div>
                    </div>
                    <div class=\"form-group position-relative has-icon-left mb-4\">
                        <input input type=\"password\" name=\"password\" id=\"inputPassword\" autocomplete=\"current-password\" required class=\"form-control form-control-xl\" placeholder=\"Mot de passe\"  style=\"background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;\">
                        <div class=\"form-control-icon\">
                            <i class=\"bi bi-shield-lock\"></i>
                        </div>
                    </div>
                    <div class=\"form-check form-check-lg d-flex align-items-end\">
                        <input class=\"form-check-input me-2\" type=\"checkbox\" value=\"\" id=\"flexCheckDefault\" name=\"_remember_me\">
                        <label class=\"form-check-label text-gray-600\" for=\"flexCheckDefault\">
                            Rester connecter
                        </label>
                    </div>
                    <input type=\"hidden\" name=\"_csrf_token\"
                           value=\"{{ csrf_token('authenticate') }}\"
                    >
                    <button class=\"btn btn-primary btn-block btn-lg shadow-lg mt-5\">Log in</button>
                </form>
                <div class=\"text-center mt-5 text-lg fs-4\">
                    <p><a class=\"font-bold\" href=\"#\">Mot de passe oublié ?</a>.</p>
                </div>
            </div>
        </div>
        <div class=\"col-lg-7 d-none d-lg-block\">
            <div id=\"auth-right\">

            </div>
        </div>
    </div>

</div>
</body>
</html>
", "security/login.html.twig", "/home/ouss/crm_j4r/templates/security/login.html.twig");
